
public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int start=1; start<10; start++) {
			if(start%2 == 0) {
				System.out.println(start + "Even");
				
			}else {
				System.out.println(start + "Odd");
			}
			
		}
	}

}
